      "value": [
          {
              "id": "088f24c1-13e7-4242-bddf-cb7aa2312f10",
              "type": "paragraph",
              "children": [
                  {
                      "text": "“Come on, Queequeg,” said I, “all right. There’s Mrs. Hussey.”"
                  }
              ],
              "props": {
                  "nodeType": "block"
              }
          }
      ],
      "type": "Paragraph",
