      "value": [
          {
              "id": "6e576b5c-209a-4b00-966e-3cb6a57266a4",
              "type": "paragraph",
              "children": [
                  {
                      "text": "“The Duke.”"
                  }
              ],
              "props": {
                  "nodeType": "block"
              }
          }
      ],
      "type": "Paragraph",
      "meta": {
          "order": 1682,
          "depth": 0
      }
