      "id": "fd4ccda1-3edc-4b15-a89a-c37c4bcfe000",
      "value": [
          {
              "id": "bf933638-b6c4-44d7-8ef2-b08dde0c432c",
              "type": "paragraph",
              "children": [
                  {
                      "text": "("
                  },
                  {
                      "code": true,
                      "text": "Ahab to himself"
                  },
                  {
                      "text": ".)"
                  }
              ],
              "props": {
                  "nodeType": "block"
              }
          }
      ],
      "type": "Paragraph",
      "meta": {
          "order": 2312,
          "depth": 0
      }
